definition.script:
pipeline {
  agent any
  options {
    disableConcurrentBuilds()
  }
  stages {
    stage('Validate Parent Workspace') {
      steps {
        script {
          if (!params.PARENT_WORKSPACE || params.PARENT_WORKSPACE.trim().isEmpty()) {
            error("PARENT_WORKSPACE parameter is required. This job should be called from an orchestrator job.")
          }
          echo "Using repos from parent workspace: ${params.PARENT_WORKSPACE}"

          // Verify monitoring_stack exists
          def repoPath = "${params.PARENT_WORKSPACE}/monitoring_stack"
          def exists = sh(script: "test -d '${repoPath}' && echo 'exists' || echo 'missing'", returnStdout: true).trim()
          if (exists == 'missing') {
            error("Required repository 'monitoring_stack' not found in parent workspace at ${repoPath}. Run GIT_MONITORING first.")
          }
          echo "‚úì Found monitoring_stack at ${repoPath}"
        }
      }
    }
    stage('Copy Monitoring Files to EC2') {
      steps {
        script {
          def terraformBin = sh(script: '''which terraform 2>/dev/null || \
            (test -f /usr/local/bin/terraform && echo /usr/local/bin/terraform) || \
            (test -f /usr/bin/terraform && echo /usr/bin/terraform) || \
            (test -f /opt/homebrew/bin/terraform && echo /opt/homebrew/bin/terraform) || \
            echo "terraform"''', returnStdout: true).trim()
          def terraformDir = "${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV}"

          // Use centralized state file
          def jenkinsHome = sh(script: '''if [ "$(uname -s)" = "Darwin" ]; then 
            echo "$HOME/.jenkins"
          else
            echo "/var/lib/jenkins"
          fi''', returnStdout: true).trim()
          def stateFile = "${jenkinsHome}/workspace/terraform-states/${params.ENV}/terraform.tfstate"

          def ec2Ip = sh(
            script: "cd ${terraformDir} && ${terraformBin} output -state=${stateFile} -raw public_ip",
            returnStdout: true
          ).trim()

          echo "EC2 IP: ${ec2Ip}"

          // Get app_name from terraform output for dashboard titles
          def appName = "App"  // Default value
          try {
            appName = sh(
              script: "cd ${terraformDir} && ${terraformBin} output -state=${stateFile} -raw app_name 2>/dev/null || echo 'App'",
              returnStdout: true
            ).trim()
          } catch (Exception e) {
            echo "‚ö†Ô∏è  Could not read app_name from tfstate, using default: ${appName}"
          }

          echo "Preparing dashboards for: ${appName}"

          // Detect platform and set SSH key path
          def sshKey = sh(script: '''if [ "$(uname -s)" = "Darwin" ]; then 
            echo "$HOME/.ssh/url_app.pem"
          else
            echo "/var/lib/jenkins/.ssh/url_app.pem"
          fi''', returnStdout: true).trim()

          // Configure dashboards from config.yml
          sh """
            cd "${params.PARENT_WORKSPACE}/monitoring_stack"

            # Run configure script to replace placeholders
            chmod +x configure.sh
            ./configure.sh

            echo "‚úì Dashboards configured from config.yml"
          """

          // Update dashboard titles based on app_name
          sh """
            cd "${params.PARENT_WORKSPACE}/monitoring_stack/grafana/provisioning/dashboards"

            # Backup original files
            cp api_dashboard.json api_dashboard.json.bak
            cp app_monitoring_dashboard.json app_monitoring_dashboard.json.bak

            # Update titles dynamically
            if [ "${appName}" != "App" ]; then
              # Use app_name as prefix (e.g., "MyApp API Dashboard")
              sed -i.tmp 's/"title": "API Dashboard"/"title": "${appName} API Dashboard"/' api_dashboard.json
              sed -i.tmp 's/"title": "App Monitoring"/"title": "${appName} App Monitoring"/' app_monitoring_dashboard.json
            fi
            # If appName is "App" (default), leave titles as "API Dashboard" and "App Monitoring"

            rm -f *.tmp
          """

          sh """
            cd "${params.PARENT_WORKSPACE}/monitoring_stack"

            # Copy all monitoring files to EC2
            scp -i ${sshKey} \
              -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null \
              -r monitoring.yml prometheus/ grafana/ blackbox/ \
              ubuntu@${ec2Ip}:/home/ubuntu/

            # Restore original dashboard files
            cd grafana/provisioning/dashboards
            mv api_dashboard.json.bak api_dashboard.json
            mv app_monitoring_dashboard.json.bak app_monitoring_dashboard.json
          """
        }
      }
    }
    stage('Deploy Monitoring Stack') {
      steps {
        script {
          def terraformBin = sh(script: '''which terraform 2>/dev/null || \
            (test -f /usr/local/bin/terraform && echo /usr/local/bin/terraform) || \
            (test -f /usr/bin/terraform && echo /usr/bin/terraform) || \
            (test -f /opt/homebrew/bin/terraform && echo /opt/homebrew/bin/terraform) || \
            echo "terraform"''', returnStdout: true).trim()
          def terraformDir = "${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV}"

          // Use centralized state file
          def jenkinsHome = sh(script: '''if [ "$(uname -s)" = "Darwin" ]; then 
            echo "$HOME/.jenkins"
          else
            echo "/var/lib/jenkins"
          fi''', returnStdout: true).trim()
          def stateFile = "${jenkinsHome}/workspace/terraform-states/${params.ENV}/terraform.tfstate"

          def ec2Ip = sh(
            script: "cd ${terraformDir} && ${terraformBin} output -state=${stateFile} -raw public_ip",
            returnStdout: true
          ).trim()

          // Get app_name from terraform output, use default if not available
          def appName = "App"  // Default value
          try {
            appName = sh(
              script: "cd ${terraformDir} && ${terraformBin} output -state=${stateFile} -raw app_name 2>/dev/null || echo 'App'",
              returnStdout: true
            ).trim()
          } catch (Exception e) {
            echo "‚ö†Ô∏è  Could not read app_name from tfstate, using default: ${appName}"
          }

          echo "Deploying monitoring for: ${appName}"

          // Detect platform and set SSH key path
          def sshKey = sh(script: '''if [ "$(uname -s)" = "Darwin" ]; then 
            echo "$HOME/.ssh/url_app.pem"
          else
            echo "/var/lib/jenkins/.ssh/url_app.pem"
          fi''', returnStdout: true).trim()

          sh """
            ssh -i ${sshKey} \
              -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null \
              ubuntu@${ec2Ip} << 'ENDSSH'
              cd /home/ubuntu

              # Ensure application network exists (created by urlshortener_docker)
              # If app is running, network already exists. If not, create it manually.
              docker network inspect monitoring >/dev/null 2>&1 || docker network create monitoring

              # Stop existing monitoring if running
              docker-compose -f monitoring.yml down 2>/dev/null || true

              # Deploy monitoring stack
              export APP_NAME="${appName}"
              docker-compose -f monitoring.yml up -d

              # Restart Grafana to reload dashboards
              docker restart grafana

              # Show status
              docker-compose -f monitoring.yml ps
ENDSSH
          """
        }
      }
    }
  }
  post {
    success {
      echo '‚úÖ Monitoring Stack deployed successfully!'
      echo 'üìä Access Grafana at: http://<EC2_IP>:3000 (admin/admin)'
      echo 'üìà Access Prometheus at: http://<EC2_IP>:9091'
    }
    failure {
      echo '‚ùå Monitoring Stack deployment failed. Check the logs above.'
    }
  }
}
