definition.script:
pipeline {
  agent any
  stages {
    stage('Clone Deploy Scripts') {
      steps {
        sh 'git clone https://github.com/inboxtoshashi/deploy_url_app.git $WORKSPACE/deploy_url_app'
      }
    }
    stage('Get EC2 IP and Copy Monitoring Script') {
      steps {
        script {
          def terraformBin = sh(script: 'which terraform || echo /opt/homebrew/bin/terraform', returnStdout: true).trim()
          def terraformDir = "${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV}"
          def stateFile = "${params.PARENT_WORKSPACE}/tfstate-${params.ENV}/terraform.tfstate"
          def ec2Ip = sh(
            script: "cd ${terraformDir} && ${terraformBin} output -state=${stateFile} -raw public_ip",
            returnStdout: true
          ).trim()

          echo "EC2 IP: ${ec2Ip}"

          sh """
            cd \$WORKSPACE/deploy_url_app
            scp -i /var/lib/jenkins/.ssh/url_app.pem \
              -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null \
              deploy_monitoring/deploy_monitoring.sh \
              ubuntu@${ec2Ip}:/home/ubuntu/
          """
        }
      }
    }
    stage('Deploy Monitoring Stack') {
      steps {
        script {
          def terraformBin = sh(script: 'which terraform || echo /opt/homebrew/bin/terraform', returnStdout: true).trim()
          def terraformDir = "${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV}"
          def stateFile = "${params.PARENT_WORKSPACE}/tfstate-${params.ENV}/terraform.tfstate"
          def ec2Ip = sh(
            script: "cd ${terraformDir} && ${terraformBin} output -state=${stateFile} -raw public_ip",
            returnStdout: true
          ).trim()

          def appName = sh(
            script: "cd ${terraformDir} && ${terraformBin} output -state=${stateFile} -raw app_name",
            returnStdout: true
          ).trim()

          echo "Deploying monitoring for: ${appName}"

          sh """
            ssh -i /var/lib/jenkins/.ssh/url_app.pem \
              -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null \
              ubuntu@${ec2Ip} << 'ENDSSH'
              cd /home/ubuntu
              dos2unix deploy_monitoring.sh || true
              chmod +x deploy_monitoring.sh
              export APP_NAME="${appName}"
              ./deploy_monitoring.sh
ENDSSH
          """
        }
      }
    }
  }
  post {
    success {
      echo 'âœ… Monitoring Stack deployed successfully!'
      echo 'ðŸ“Š Access Grafana at: http://<EC2_IP>:3000 (admin/admin)'
      echo 'ðŸ“ˆ Access Prometheus at: http://<EC2_IP>:9091'
    }
    failure {
      echo 'âŒ Monitoring Stack deployment failed. Check the logs above.'
    }
  }
}
