definition.script:
pipeline { agent any
stages { stage('Destroy Infra') { steps { script { def terraformBin = sh(script: '''which terraform 2>/dev/null || \ (test -f /usr/local/bin/terraform && echo /usr/local/bin/terraform) || \ (test -f /usr/bin/terraform && echo /usr/bin/terraform) || \ (test -f /opt/homebrew/bin/terraform && echo /opt/homebrew/bin/terraform) || \ echo "terraform"''', returnStdout: true).trim() echo "Using Terraform: ${terraformBin}"
withCredentials([[ $class: 'AmazonWebServicesCredentialsBinding', credentialsId: 'aws-creds', accessKeyVariable: 'AWS_ACCESS_KEY_ID', secretKeyVariable: 'AWS_SECRET_ACCESS_KEY' ]]) {
sh """ # Detect OS for path configuration OS=\$(uname -s) if [ "\$OS" = "Darwin" ]; then JENKINS_HOME="\$HOME/.jenkins" else JENKINS_HOME="/var/lib/jenkins" fi
STATE_FILE="\${JENKINS_HOME}/workspace/terraform-states/${params.ENV}/terraform.tfstate"
# Backup existing state before destroy if [ -f "\${STATE_FILE}" ]; then echo "ðŸ“¦ Backing up terraform state before destroy..." ${params.PARENT_WORKSPACE}/url_infra/scripts/backup_tfstate.sh ${params.ENV} \${STATE_FILE} fi
# Run terraform cd ${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV} export AWS_ACCESS_KEY_ID="\${AWS_ACCESS_KEY_ID}" export AWS_SECRET_ACCESS_KEY="\${AWS_SECRET_ACCESS_KEY}" ${terraformBin} init """
if (params.DESTROY_ACTION == 'PLAN') { sh """ # Detect OS for path configuration OS=\$(uname -s) if [ "\$OS" = "Darwin" ]; then JENKINS_HOME="\$HOME/.jenkins" else JENKINS_HOME="/var/lib/jenkins" fi
STATE_FILE="\${JENKINS_HOME}/workspace/terraform-states/${params.ENV}/terraform.tfstate"
cd ${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV} export AWS_ACCESS_KEY_ID="\${AWS_ACCESS_KEY_ID}" export AWS_SECRET_ACCESS_KEY="\${AWS_SECRET_ACCESS_KEY}" ${terraformBin} plan \\ -var-file=../${params.ENV}.tfvars \\ -state=\${STATE_FILE} \\ -destroy """ } else { sh """ # Detect OS for path configuration OS=\$(uname -s) if [ "\$OS" = "Darwin" ]; then JENKINS_HOME="\$HOME/.jenkins" else JENKINS_HOME="/var/lib/jenkins" fi
STATE_FILE="\${JENKINS_HOME}/workspace/terraform-states/${params.ENV}/terraform.tfstate"
cd ${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV} export AWS_ACCESS_KEY_ID="\${AWS_ACCESS_KEY_ID}" export AWS_SECRET_ACCESS_KEY="\${AWS_SECRET_ACCESS_KEY}" ${terraformBin} destroy \\ -var-file=../${params.ENV}.tfvars \\ -state=\${STATE_FILE} \\ -auto-approve """ }
} } } } } }
