definition.script:
pipeline {
  agent any

  stages {
    stage('Destroy Infra') {
      steps {
        script {
          def terraformBin = sh(script: 'which terraform || echo /opt/homebrew/bin/terraform', returnStdout: true).trim()
          echo "Using Terraform: ${terraformBin}"

          withCredentials([[
            $class: 'AmazonWebServicesCredentialsBinding',
            credentialsId: 'aws-creds',
            accessKeyVariable: 'AWS_ACCESS_KEY_ID',
            secretKeyVariable: 'AWS_SECRET_ACCESS_KEY'
          ]]) {

            sh """
              # Backup existing state before destroy
              if [ -f "${params.PARENT_WORKSPACE}/tfstate-${params.ENV}/terraform.tfstate" ]; then
                TIMESTAMP=\$(date +%Y%m%d_%H%M%S)
                mkdir -p ${params.PARENT_WORKSPACE}/tfstate-${params.ENV}-backups
                cp ${params.PARENT_WORKSPACE}/tfstate-${params.ENV}/terraform.tfstate \\
                   ${params.PARENT_WORKSPACE}/tfstate-${params.ENV}-backups/terraform.tfstate.\${TIMESTAMP}
                echo "âœ… Backed up state to tfstate-${params.ENV}-backups/terraform.tfstate.\${TIMESTAMP}"
              fi

              # Run terraform
              cd ${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV}
              export AWS_ACCESS_KEY_ID="\${AWS_ACCESS_KEY_ID}"
              export AWS_SECRET_ACCESS_KEY="\${AWS_SECRET_ACCESS_KEY}"
              ${terraformBin} init
            """

            if (params.CONFIRM_DESTROY == 'YES') {
              sh """
                cd ${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV}
                export AWS_ACCESS_KEY_ID="\${AWS_ACCESS_KEY_ID}"
                export AWS_SECRET_ACCESS_KEY="\${AWS_SECRET_ACCESS_KEY}"
                ${terraformBin} plan \\
                  -var-file=../${params.ENV}.tfvars \\
                  -state=${params.PARENT_WORKSPACE}/tfstate-${params.ENV}/terraform.tfstate \\
                  -destroy
              """
            } else {
              sh """
                cd ${params.PARENT_WORKSPACE}/url_infra/labs/${params.ENV}
                export AWS_ACCESS_KEY_ID="\${AWS_ACCESS_KEY_ID}"
                export AWS_SECRET_ACCESS_KEY="\${AWS_SECRET_ACCESS_KEY}"
                ${terraformBin} destroy \\
                  -var-file=../${params.ENV}.tfvars \\
                  -state=${params.PARENT_WORKSPACE}/tfstate-${params.ENV}/terraform.tfstate \\
                  -auto-approve
              """
            }

          }
        }
      }
    }
  }
}
