definition.script:
pipeline {
  agent any
  options {
    disableConcurrentBuilds()
  }
  stages {
    stage('Clone Application') {
      steps {
        sh '''
          REF="${URL_APP_REF:-main}"
          echo "Target reference: ${REF}"

          if [ -d "${PARENT_WORKSPACE}/deploy_url_app/.git" ]; then
            echo "Repository exists, updating..."
            cd ${PARENT_WORKSPACE}/deploy_url_app
            git fetch --all --tags --prune
            git remote set-head origin --auto >/dev/null 2>&1 || true
            # Reset any local changes
            git reset --hard HEAD
          else
            echo "Repository does not exist, cloning..."
            git clone https://github.com/inboxtoshashi/deploy_url_app.git ${PARENT_WORKSPACE}/deploy_url_app
            cd ${PARENT_WORKSPACE}/deploy_url_app
          fi

          # Try to checkout - git will handle branches, tags, and commits
          echo "Attempting to checkout: ${REF}"
          if git checkout "${REF}" 2>/dev/null; then
            echo "✓ Successfully checked out ${REF}"
            # Pull latest changes for this ref
            git pull origin "${REF}" 2>/dev/null || echo "Already up to date"
          elif git checkout -b "${REF}" "origin/${REF}" 2>/dev/null; then
            echo "✓ Successfully checked out remote branch ${REF}"
          else
            echo "✗ Error: '${REF}' not found in repository"
            echo "Available branches:"
            git branch -r | head -10
            exit 1
          fi

          echo "Current commit:"
          git log -1 --oneline
        '''
      }
    }
  }
  post {
    success {
      echo '✅ Deploy URL App repository cloned successfully!'
    }
    failure {
      echo '❌ Failed to clone Deploy URL App repository.'
    }
  }
}
