definition.script:
pipeline {
  agent any
  stages {
    stage('Clone Deploy URL App') {
      steps {
        sh 'git clone https://github.com/inboxtoshashi/deploy_url_app.git $WORKSPACE/deploy_url_app'
      }
    }
  }
  post {
    success {
      echo '✅ Deploy URL App repository cloned successfully!'
    }
    failure {
      echo '❌ Failed to clone Deploy URL App repository.'
    }
  }
}
