definition.script:
pipeline {
  agent any
  stages {
    stage('Clone Monitoring Stack') {
      steps {
        sh 'git clone https://github.com/inboxtoshashi/monitoring_stack.git $WORKSPACE/monitoring_stack'
      }
    }
  }
  post {
    success {
      echo '✅ Monitoring Stack repository cloned successfully!'
    }
    failure {
      echo '❌ Failed to clone Monitoring Stack repository.'
    }
  }
}
