definition.script:
pipeline {
  agent any
  stages {
    stage('Clone App') {
      steps {
        sh 'git clone https://github.com/inboxtoshashi/deploy_url_app.git $WORKSPACE/deploy_url_app'
      }
    }
    stage('Run main.sh') {
      steps {
        sh 'cd $WORKSPACE/deploy_url_app && ./main.sh'
      }
    }
  }
}
