definition.script:
pipeline {
  agent any

  stages {
    stage('Deploy Infra') {
      steps {
        script {
          withCredentials([[$class: 'AmazonWebServicesCredentialsBinding', credentialsId: 'aws-creds']]) {

            sh "cd labs/${params.ENV} && terraform init"

            if (params.CONFIRM_APPLY == 'YES') {
              sh "cd labs/${params.ENV} && terraform plan"
            } else {
              sh "cd labs/${params.ENV} && terraform apply -auto-approve"
            }

          }
        }
      }
    }
  }
}
